<script>
import { store } from "../store.js";
import axios from "axios";
import useValidate from "@vuelidate/core";
import Final from "./Final.vue";

import {
  required,
  minLength,
  maxLength,
  alphaNum,
  email,
} from "@vuelidate/validators";

import Map from "./Map.vue";
// import mapboxgl from "mapbox-gl";
// import { GeolocateControl } from "mapbox-gl-controls";

export default {
  data() {
    return {
      v$: useValidate(),
      store,
      reg: "",
      link: "",
      email: "",
      eemail: "",
      co2: "",
      showSpinner: false,
      showShit: true,
      rating: 3.8,
      showFinal: false,
      firstName: "",
      lastName: "",
      fullName: "",
      uEmail: "",
      uFirstName: "",
      uLastName: "",
    };
  },
  validations() {
    return {
      reg: {
        required,
        minLength: minLength(7),
        maxLength: maxLength(7),
        alphaNum,
      },
      uFirstName: { required },
      uLastName: { required },
      uEmail: { required, email },
    };
  },
  components: {
    Map,
    Final,
  },
  methods: {
    denySharing() {
      this.getFakeData();
    },
    submitForm() {
      this.v$.$validate();
      if (this.v$.reg.$error) {
        alert("Please enter a 7 letter alphanumerical reg code");
      } else if (this.v$.uFirstName.$error) {
        alert("Please enter a first name");
      } else if (this.v$.uLastName.$error) {
        alert("Please enter a last name");
      } else if (this.v$.uEmail.$error) {
        alert("Please enter a valid email");
      } else {
        store.reg = this.reg;
        let code = store.loccode;
        this.link = `https://www.google.com/maps/search/${code}`;
        this.getFakeData();
      }
    },
    async getFakeData() {
      if (store.distance != 0) {
        const res = await fetch("https://randomuser.me/api");
        const { results } = await res.json();
        this.firstName = results[0].name.first;
        this.lastName = results[0].name.last;
        store.email = results[0].email;
        store.age = results[0].dob.age;
        store.img = results[0].picture.large;
        let gen = results[0].gender;
        let fname = this.firstName.toLowerCase();
        let lname = this.lastName.toLowerCase();
        let ffname = this.firstName;
        let llname = this.lastName;
        this.fullName = `${ffname} ${llname}`;

        this.eemail = `${fname}.${lname}@gmail.com`;
        this.email = `mailto: ${this.eemail}`;
        console.log(this.email);

        if (gen === "male") {
          store.gender = true;
        } else {
          store.gender = false;
        }

        const lon = store.long;
        const lat = store.lat;

        axios
          .get(
            `https://api.postcodes.io/postcodes?lon=${lon}&lat=${lat}&widesearch=true&limit=1`
          )
          .then(function (response) {
            store.loccode = response.data.result[0].postcode;
            // handle success
          })
          .catch(function (error) {
            // handle error
            console.log(error);
          })
          .then(function () {
            // always executed
          });
        store.show2 = false;
        this.showSpinner = true;
        this.showShit = false;
        setTimeout(this.showusShitus, 1000);

        this.getDistSaved();
      } else {
        alert("Please your destination on the map.");
      }
    },
    showusShitus() {
      this.showSpinner = false;
      store.show2 = true;
    },
    getDistSaved() {
      let dist = store.distance;

      let sub = dist / (Math.floor(Math.random() * 10) + 1);

      let distSaved = dist - sub;

      store.distSaved = distSaved.toString();

      store.showDistSaved = true;
    },
  },
};
</script>

